import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import BeachProfiles from './components/BeachProfiles';
import MarinaBeach from './components/MarinaBeach';
import KovalamBeach from './components/KovalamBeach';
import ElliotBeach from './components/ElliotBeach';
import KanyakumariBeach from './components/KanyakumariBeach';
import RameswaramBeach from './components/RameswaramBeach';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <h1>Beach Profiles</h1>

        <Routes>
          <Route path="/" element={<BeachProfiles />} />
          <Route path="/marina" element={<MarinaBeach />} />
          <Route path="/kovalam" element={<KovalamBeach />} />
          <Route path="/elliot" element={<ElliotBeach />} />
          <Route path="/kanyakumari" element={<KanyakumariBeach />} />
          <Route path="/rameswaram" element={<RameswaramBeach />} />
        </Routes>

        {/* Decorative Icons (Always Visible) */}
        <div className="beach-decor">
          <div className="svg shell">
            <svg viewBox="0 0 64 64" width="60" height="60">
              <path fill="#f4c2c2" d="M32 2C15 5 4 30 4 30s8 28 28 28 28-28 28-28S49 5 32 2z"/>
            </svg>
          </div>

          <div className="svg starfish">
            <svg viewBox="0 0 64 64" width="60" height="60">
              <path fill="#ffcc80" d="M32 2l5 18 18 2-14 11 5 18-14-10-14 10 5-18-14-11 18-2z"/>
            </svg>
          </div>

          <div className="svg coral">
            <svg viewBox="0 0 64 64" width="60" height="60">
              <path fill="#ff8a80" d="M30 2c-2 20-6 20-8 26-2 6 4 6 4 12s-6 6-6 12 6 10 6 10h4s6-4 6-10-6-6-6-12 6-6 4-12c-2-6-6-6-8-26z"/>
            </svg>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
