import React from 'react';


const beaches = [
  {
    name: "Marina Beach",
  },
  {
    name: "Elliot’s Beach",
  },
  {
    name: "Mahabalipuram Beach",
    location: "Mahabalipuram",
   // image: require('../images/mahabalipuram.jpg'),
    highlights: "Near Shore Temple, sculptures, and cultural heritage.",
    mapLink: "https://maps.google.com/?q=Mahabalipuram+Beach"
  },
  {
    name: "Kovalam Beach",
    location: "Covelong, near Chennai",
   // image: require('../images/kovalam.jpg'),
    highlights: "Surfing, yoga, eco-tourism, less crowded.",
    mapLink: "https://maps.google.com/?q=Kovalam+Beach+Chennai"
  },
  {
    name: "Kanyakumari Beach",
    location: "Kanyakumari",
   // image: require('../images/kanyakumari.jpg'),
    highlights: "Sunrise/sunset point, Vivekananda Rock, confluence of 3 seas.",
    mapLink: "https://maps.google.com/?q=Kanyakumari+Beach"
  }
  ,
  {
  name: "Rameswaram Beach",
  location: "Rameswaram, Ramanathapuram District",
  //image: require('../images/rameswaram.jpg'), // Add the image to your images folder
  highlights: "Calm waters, coral reefs, sacred pilgrimage site, near Pamban Bridge.",
  mapLink: "https://maps.google.com/?q=Rameswaram+Beach"
}
];

function BeachProfiles() {
  return (
    <div className="beach-container">
      {beaches.map((beach, idx) => (
        <div className="beach-card" key={idx}>
          <h2>{beach.name}</h2>
        </div>
      ))} 
    </div>
  );
}

export default BeachProfiles;
