import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';


const beaches = [
  { name: 'Marina Beach', imageClass: 'marina', path: '/marina' },
  { name: 'Kovalam Beach', imageClass: 'kovalam', path: '/kovalam' },
  { name: 'Elliot’s Beach', imageClass: 'elliot', path: '/elliot' },
  { name: 'Kanyakumari Beach', imageClass: 'kanyakumari', path: '/kanyakumari' },
  { name: 'Rameswaram Beach', imageClass: 'rameswaram', path: '/rameswaram' },
];

function BeachProfiles() {
  const [activeIndex, setActiveIndex] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prev) => (prev + 1) % beaches.length);
    }, 3000); // show each card for 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="beach-container">
      {beaches.map((beach, index) => (
        <div
          key={beach.name}
          className={`beach-card ${beach.imageClass} ${activeIndex === index ? 'active' : 'inactive'}`}
          onClick={() => navigate(beach.path)}
        >
          <h2>{beach.name}</h2>
        </div>
      ))}
    </div>
  );
}

export default BeachProfiles;
