import React from 'react';
import './App.css'; // You'll create this CSS file

const beaches = [
  {
    name: "Marina Beach",
    location: "Chennai",
    //image: require('../images/marina.jpg'),
    highlights: "Longest urban beach, iconic promenade, historical importance.",
    mapLink: "https://maps.google.com/?q=Marina+Beach+Chennai"
  },
  {
    name: "Elliot’s Beach",
    location: "Besant Nagar, Chennai",
   // image: require('../images/elliots.jpg'),
    highlights: "Calm, clean, perfect for families and evening walks.",
    mapLink: "https://maps.google.com/?q=Elliots+Beach+Chennai"
  },
  {
    name: "Mahabalipuram Beach",
    location: "Mahabalipuram",
   // image: require('../images/mahabalipuram.jpg'),
    highlights: "Near Shore Temple, sculptures, and cultural heritage.",
    mapLink: "https://maps.google.com/?q=Mahabalipuram+Beach"
  },
  {
    name: "Kovalam Beach",
    location: "Covelong, near Chennai",
   // image: require('../images/kovalam.jpg'),
    highlights: "Surfing, yoga, eco-tourism, less crowded.",
    mapLink: "https://maps.google.com/?q=Kovalam+Beach+Chennai"
  },
  {
    name: "Kanyakumari Beach",
    location: "Kanyakumari",
   // image: require('../images/kanyakumari.jpg'),
    highlights: "Sunrise/sunset point, Vivekananda Rock, confluence of 3 seas.",
    mapLink: "https://maps.google.com/?q=Kanyakumari+Beach"
  }
];

function BeachProfiles() {
  return (
    <div className="beach-container">
      {beaches.map((beach, idx) => (
        <div className="beach-card" key={idx}>
          <img src={beach.image} alt={beach.name} className="beach-img" />
          <div className="beach-info">
            <h2>{beach.name}</h2>
            <p className="location">{beach.location}</p>
            <p className="highlights">{beach.highlights}</p>
            <a href={beach.mapLink} target="_blank" rel="noopener noreferrer">
              View on Map
            </a>
          </div>
        </div>
      ))}
    </div>
  );
}

export default BeachProfiles;
